var pagesInteration={loader:function(){},initialPage:function(){$.ajax({type:"POST",url:root+"pages/home.html",error:function(){alert("error")},beforeSend:function(){},success:function(e){$(".section-default").html(e),setTimeout(function(){$(".pages-default").removeClass("hidden")},100)}})},getPages:function(){var e=$(this).attr("data-target");$.ajax({type:"POST",url:root+"pages/"+e,error:function(){alert("error")},beforeSend:function(){},success:function(e){$(".section-default").html(e),setTimeout(function(){$(".pages-default").removeClass("hidden")},100)}})},ready:function(){$('[data-class="itemTopMenu"]').click(pagesInteration.getPages),pagesInteration.initialPage(),pagesInteration.loader()}};$(document).ready(pagesInteration.ready);